mapper.select:
select
shop_name shopName,
sum(total_price) orderTotalPrice,
count(id) orderNumber,
b.refundOrderNumber refundOrderNumber,
b.refundOrderTotalPrice refundOrderTotalPrice
from
pre_order_info a
left join
(select shop_id , sum(total_price) refundOrderTotalPrice,count(id) refundOrderNumber
from pre_refund_order_info
where shop_id is not null

    and shop_id = #{param.shopId}


    AND create_time >= #{param.createStartTime}


    AND create_time <= #{param.createEndTime}

group by shop_id
) b on a.shop_id=b.shop_id
where a.order_state not in (1)

    and a.shop_id = #{param.shopId}


    AND a.create_time >= #{param.createStartTime}


    AND a.create_time <= #{param.createEndTime}

group by a.shop_id
order by a.create_time desc

select
a.guide_name guideName,
count(a.id)- IFNULL(b.refundOrderNumber,0)  orderNumber,
sum(a.total_price)-IFNULL(b.refundOrderTotalPrice,0)  orderPrice,
IFNULL(mem.num,0) newCustomerNum
from pre_order_info a
left join (
select guide_id , sum(total_price) refundOrderTotalPrice,count(id) refundOrderNumber
from pre_refund_order_info
where guide_id is not null

    and guide_name = #{param.guideName}


    AND create_time >= #{param.createStartTime}


    AND create_time <= #{param.createEndTime}

group by guide_id
)b on b.guide_id=a.guide_id
left join(select guide_id,count(id) num from member_info)  mem
on mem.guide_id=a.guide_id
where a.order_state=6

    and a.guide_name = #{param.guideName}


    AND a.create_time >= #{param.createStartTime}


    AND a.create_time <= #{param.createEndTime}

group by a.guide_id
order by a.create_time desc

SELECT
order_info.*
FROM
`pre_order_info` order_info

    RIGHT JOIN `pre_refund_order_info` refund
    ON order_info.id = refund.order_id

where 1=1

    and order_info.guide_id = #{param.guideId}


    and order_info.member_id = #{param.memberId}


    AND order_info.order_state = #{param.orderState}


    AND order_info.buyer_phone = #{param.buyerPhone}


    AND order_info.last_update_time >= #{param.startTime}


    AND order_info.last_update_time <= #{param.endTime}

order by order_info.last_update_time desc

select a.delivery_district deliveryDistrict, a.delivery_province deliveryProvince, a.delivery_city deliveryCity, a.order_code orderCode, a.create_time createTime, a.reserve_start_time reserveStartTime, a.delivery_time takeTime, b.order_state orderState, a.reserve_id memberId, a.reserve_name buyerName, a.reserve_phone buyerPhone, a.reserve_shop shopName, a.guide_name guideName, a.goods_code goodsCode, a.goods_name goodsName, count(a.id) orderNumber, a.goods_price goodsPrice, a.goods_price unitPrice from pre_order_goods a left join pre_order_info b on b.id=a.order_id where a.id is not null
and a.shop_id= #{param.shopId}
and a.guide_name= #{param.guideName}
and b.order_state= #{param.orderState.type}
and a.order_code= #{param.orderCode}
and a.reserve_name= #{param.buyerName}
AND a.create_time >= #{param.createStartTime}
AND a.create_time <= #{param.createEndTime}
group by a.id order by a.create_time desc

select a.buyer_district deliveryDistrict, a.buyer_province deliveryProvince, a.buyer_city deliveryCity, a.order_code orderCode, a.shop_name shopName, a.order_state orderState, a.guide_name guideName, d.goodsCode goodsCode, d.goods_name goodsName, d.num orderNumber, d.goodsPrice goodsPrice, d.goodsPrice unitPrice, a.total_price totaGoodsPrice from pre_order_info a
left join (select count(c.id) num,c.order_id orderId, b.goods_price goodsPrice, b.goods_price unitPrice,b.goods_code goodsCode,c.goods_name from pre_order_goods c left join pre_goods_info b on b.goods_code=c.goods_code where c.goods_type not in (2) ) d on d.orderId=a.id where a.id is not null
and a.shop_id= #{param.shopId}
and a.guide_name= #{param.guideName}
and a.order_state= #{param.orderState.type}
and a.order_code= #{param.orderCode}
and a.buyer_name= #{param.buyerName}
AND a.create_time >= #{param.createStartTime}
AND a.create_time <= #{param.createEndTime}
order by a.create_time desc
